import React, { useContext } from 'react';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { UserContext } from '../App';

const NavBar = () => {
  const history = useHistory();
  const { state, dispatch } = useContext(UserContext);
  const location = useLocation();
  const currentUser = localStorage.getItem("currentUser");

  const handleLogout = () => {
    localStorage.clear();
    dispatch({ type: "CLEAR" });
    history.push('/signin');
  };

  return (
    <nav className='navbar navbar-dark' style={{ display: "grid", gridTemplateRows: "1fr", backgroundColor: "#c37f1a" }}>
      <div className="nav-wrapper" style={{ display: "grid", gridTemplateColumns: "1fr 1fr", alignItems: "center" }}>

        <div>
          <Link to={state ? "/" : "/signin"} className="brand-logo center"
            style={{ color: "white", fontSize: "30px", textDecoration: "none", fontFamily: "Acme" }}>
            Online Voting System
          </Link>
        </div>

        <div style={{ marginLeft: "600px" }}>
          {state && state.isAdmin && (
            <li
              style={{ fontSize: "24px", fontWeight: "600", color: "white", listStyleType: "none" }}
            >
              <Link to="/result" style={{ color: "white", textDecoration: "none", fontSize: "20px", fontWeight: "600", fontFamily: "Acme" }}>
                <i className="fas fa-poll"></i> {" "}Result
              </Link>
            </li>
          )}

          {state ? (
            <li
              style={{ fontSize: "24px", fontWeight: "600", color: "white", listStyleType: "none", cursor: "pointer", fontFamily: "Acme" }}
              onClick={handleLogout}
            >
              <i className="far fa-sign-out "></i> {" "} Logout
            </li>
          ) : (
            <>
              {(location.pathname === '/signin' || location.pathname === '/signup') && (
                <div style={{ justifySelf: "end", marginRight: "20px" }}>
                  <Link to="/signin" style={{ fontSize: "20px", fontWeight: "600", color: 'white', marginRight: "20px", fontFamily: "Acme" }}>Signin</Link>
                  <Link to="/signup" style={{ fontSize: "20px", fontWeight: "600", color: 'white', fontFamily: "Acme" }}>Signup</Link>
                </div>
              )}
            </>
          )}
        </div>

      </div>
    </nav>
  );
}

export default NavBar;
