// components/PostTable.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PostTable = () => {
  const [posts, setPosts] = useState([]);
  const [newPost, setNewPost] = useState({ title: '', pic: '' });
  const [editingPostId, setEditingPostId] = useState(null);

  // Fetch all posts when the component mounts
  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = () => {
    // Fetch all posts
    axios.get('http://localhost:5000/allpost')
      .then(response => {
        setPosts(response.data.posts);
      })
      .catch(error => console.error('Error fetching posts:', error));
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewPost(prevState => ({ ...prevState, [name]: value }));
  };

  const handleCreatePost = () => {
    // Create a new post
    axios.post('http://localhost:5000/createpost', newPost)
      .then(response => {
        setNewPost({ title: '', pic: '' });
        fetchPosts(); // Refresh the posts after creating a new one
      })
      .catch(error => console.error('Error creating post:', error));
  };

  const handleDeletePost = (postId) => {
    // Delete a post
    axios.delete(`http://localhost:5000/deletepost/${postId}`)
      .then(response => {
        if (response.data.error) {
          console.error(response.data.error);
        } else {
          fetchPosts(); // Refresh the posts after deleting one
        }
      })
      .catch(error => console.error('Error deleting post:', error));
  };

  const handleEditPost = (postId) => {
    // Set the post id to be edited
    setEditingPostId(postId);
    // Fetch the post details to populate the editing form
    axios.get(`http://localhost:5000/posts/${postId}`)
      .then(response => {
        setNewPost(response.data.post);
      })
      .catch(error => console.error('Error fetching post details for editing:', error));
  };

  const handleUpdatePost = () => {
    // Update the edited post
    axios.put(`http://localhost:5000/updatepost/${editingPostId}`, newPost)
      .then(response => {
        if (response.data.error) {
          console.error(response.data.error);
        } else {
          setNewPost({ title: '', pic: '' });
          setEditingPostId(null);
          fetchPosts(); // Refresh the posts after updating
        }
      })
      .catch(error => console.error('Error updating post:', error));
  };

  return (
    <div>
      <h2>Posts</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Image</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {posts.map(post => (
            <tr key={post._id}>
              <td>{post.title}</td>
              <td><img src={post.photo} alt={post.title} style={{ width: '50px', height: '50px' }} /></td>
              <td>
                <button onClick={() => handleDeletePost(post._id)}>Delete</button>
                <button onClick={() => handleEditPost(post._id)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingPostId ? (
        <div>
          <h2>Edit Post</h2>
          <label>Title:
            <input type="text" name="title" value={newPost.title} onChange={handleInputChange} />
          </label>
          <label>Image URL:
            <input type="text" name="pic" value={newPost.pic} onChange={handleInputChange} />
          </label>
          <button onClick={handleUpdatePost}>Update Post</button>
        </div>
      ) : (
        <div>
          <h2>Create New Post</h2>
          <label>Title:
            <input type="text" name="title" value={newPost.title} onChange={handleInputChange} />
          </label>
          <label>Image URL:
            <input type="text" name="pic" value={newPost.pic} onChange={handleInputChange} />
          </label>
          <button onClick={handleCreatePost}>Add Post</button>
        </div>
      )}
    </div>
  );
};

export default PostTable;
