// NavBar.js

import React, { useContext, useEffect } from 'react';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { UserContext } from '../App';

const NavBar = () => {
  const history = useHistory();
  const { state, dispatch } = useContext(UserContext);
  const location = useLocation();

  const handleLogout = () => {
    localStorage.clear();
    dispatch({ type: "CLEAR" });
    history.push('/signin');
  };

  useEffect(() => {
    const hamburger = document.querySelector('.header .nav-bar .nav-list .hamburger');
    const mobile_menu = document.querySelector('.header .nav-bar .nav-list ul');
    const menu_item = document.querySelectorAll('.header .nav-bar .nav-list ul li a');
    const header = document.querySelector('.header.container');

    const handleHamburgerClick = () => {
      hamburger.classList.toggle('active');
      mobile_menu.classList.toggle('active');
    };

    const handleScroll = () => {
      const scroll_position = window.scrollY;
      if (scroll_position > 250) {
        header.style.backgroundColor = '#29323c';
      } else {
        header.style.backgroundColor = 'transparent';
      }
    };

    const handleMenuItemClick = () => {
      hamburger.classList.toggle('active');
      mobile_menu.classList.toggle('active');
    };

    hamburger.addEventListener('click', handleHamburgerClick);

    document.addEventListener('scroll', handleScroll);

    menu_item.forEach((item) => {
      item.addEventListener('click', handleMenuItemClick);
    });

    return () => {
      hamburger.removeEventListener('click', handleHamburgerClick);
      document.removeEventListener('scroll', handleScroll);
      menu_item.forEach((item) => {
        item.removeEventListener('click', handleMenuItemClick);
      });
    };
  }, []);

  return (
    <>
      <nav className='navbar navbar-dark' style={{ display: "grid", gridTemplateRows: "1fr", backgroundColor: "#c37f1a" }}>
        <div className="nav-wrapper" style={{ display: "grid", gridTemplateColumns: "1fr 1fr", alignItems: "center" }}>

          <div>
            <Link to={state ? "/" : "/signin"} className="brand-logo center"
              style={{ color: "white", marginLeft: "6px", fontSize: "30px", textDecoration: "none", fontFamily: "Acme" }}>
              Online Voting System
            </Link>
          </div>

          <div style={{ display: "flex", alignItems: "center", marginLeft: "570px" }}>
            {state && state.isAdmin && (
              <>
                <li style={{ fontSize: "24px", fontWeight: "600", color: "white", listStyleType: "none", marginRight: "22px" }}>
                  <Link to="/result" style={{ color: "white", textDecoration: "none", fontSize: "20px", fontWeight: "600", fontFamily: "Acme" }}>
                    <i className="fas fa-poll"></i> {" "} Result
                  </Link>
                </li>
                <li style={{ fontSize: "24px", fontWeight: "600", color: "white", listStyleType: "none", marginRight: "22px" }}>
                  <Link to="/Chart" style={{ color: "white", textDecoration: "none", fontSize: "20px", fontWeight: "600", fontFamily: "Acme" }}>
                    <i className="fas fa-chart-pie"></i> {" "} Chart
                  </Link>
                </li>
                <li style={{ fontSize: "24px", fontWeight: "600", color: "white", listStyleType: "none", marginRight: "22px" }}>
                  <Link to="/PostTable" style={{ color: "white", textDecoration: "none", fontSize: "20px", fontWeight: "600", fontFamily: "Acme" }}>
                    <i className="fas fa-chart-pie"></i> {" "} Update_Posts
                  </Link>
                </li>
              </>
            )}

            {state ? (
              <li style={{ fontSize: "24px", fontWeight: "600", color: "white", listStyleType: "none", cursor: "pointer", fontFamily: "Acme" }}
                onClick={handleLogout}>
                <i className="far fa-sign-out "></i> {" "} Logout
              </li>
            ) : (
              <>
                {(location.pathname === '/signin' || location.pathname === '/signup') && (
                  <div style={{ display: "flex", marginLeft: "auto" }}>
                    <Link to="/signin" style={{ fontSize: "23px", fontWeight: "600", color: 'white', marginRight: "22px", fontFamily: "Acme" }}>Signin</Link>
                    <Link to="/signup" style={{ fontSize: "23px", fontWeight: "600", color: 'white', fontFamily: "Acme" }}>Signup</Link>
                  </div>
                )}
              </>
            )}
          </div>

        </div>
      </nav>
    </>
  );
}

export default NavBar;
