import React, { useContext } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { UserContext } from '../App';

const NavBar = () => {
  const history = useHistory();
  const { state, dispatch } = useContext(UserContext);
  const currentUser = localStorage.getItem('currentUser');

  const handleLogout = () => {
    localStorage.removeItem('currentUser');
    dispatch({ type: 'CLEAR' });
    history.push('/signin');
  };

  return (
    <nav className="navbar navbar-dark" style={{ backgroundColor: '#c37f1a' }}>
      <div className="nav-wrapper blue" style={{ display: 'flex', flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <Link to={state ? '/' : '/signin'} className="brand-logo center" style={{ color: 'white', fontSize: '30px', textDecoration: 'none', fontFamily: 'Acme' }}>
            Online Voting System
          </Link>
        </div>
        <div style={{ marginLeft: '60px' }}>
          {state && state.isAdmin && (
            <li style={{ fontSize: '22px', fontWeight: '600', color: 'white', float: 'right', listStyleType: 'none' }}>
              <Link to="/result" style={{ color: 'white', textDecoration: 'none', fontSize: '20px', fontWeight: '600' }}>
                <i className="fas fa-poll"></i> {' '}Result
              </Link>
            </li>
          )}
        </div>
        <div style={{ marginLeft: '60px' }}>
          {currentUser && currentUser !== 'null' ? (
            <li style={{ fontSize: '22px', fontWeight: '600', color: 'white', float: 'right', listStyleType: 'none', cursor: 'pointer' }} onClick={handleLogout}>
              <i className="far fa-sign-out "></i> {' '} Logout
            </li>
          ) : (
            <>
              <li>
                <Link to="/signin" style={{ fontSize: '20px', fontWeight: '600' }}>
                  Signin
                </Link>
              </li>
              <li>
                <Link to="/signup" style={{ fontSize: '20px', fontWeight: '600' }}>
                  Signup
                </Link>
              </li>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
