import React, { useContext } from 'react';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { UserContext } from '../App';

const NavBar = () => {
  const history = useHistory();
  const { state, dispatch } = useContext(UserContext);
  const location = useLocation();
  const currentUser = localStorage.getItem("currentUser");

  const handleLogout = () => {
    localStorage.clear();
    dispatch({ type: "CLEAR" });
    history.push('/signin');
  };

  return (
    <nav className='navbar navbar-dark bg-success'>
      <div className="nav-wrapper blue" style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>

        <div>
          <Link to={state ? "/" : "/signin"} className="brand-logo center"
            style={{ color: "white", fontSize: "30px", textDecoration: "none", fontFamily: "Acme" }}>
            Online Voting System
          </Link>
        </div>

        <div>
          {state && state.isAdmin && (
            <li style={{ fontSize: "22px", fontWeight: "600", color: "white", listStyleType: "none" }}>
              <Link to="/result" style={{ color: "white", textDecoration: "none", fontSize: "20px", fontWeight: "600" }}>
                <i className="fas fa-poll"></i> {" "}Result
              </Link>
            </li>
          )}

          {state ? (
            <li style={{ fontSize: "22px", fontWeight: "600", color: "white", listStyleType: "none", cursor: "pointer" }} onClick={handleLogout}>
              <i className="far fa-sign-out "></i> {" "} Logout
            </li>
          ) : (
            (location.pathname === '/signin' || location.pathname === '/signup') && (
              <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center' }}>
                <Link to="/signin" style={{ fontSize: "20px", fontWeight: "600", color: 'white', marginRight: "20px" }}>Signin</Link>
                <Link to="/signup" style={{ fontSize: "20px", fontWeight: "600", color: 'white' }}>Signup</Link>
              </div>
            )
          )}
        </div>

      </div>
    </nav>
  );
}

export default NavBar;
