import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';

const Chart = () => {
  const [element, setElement] = useState([]);
  const [labels, setLabels] = useState([]);
  const [datasets, setDatasets] = useState([
    {
      label: 'Covid-19',
      backgroundColor: ['#B21F00', '#161750', '#12BD93', '#57BD12'],
      borderColor: 'rgba(0,0,0,1)',
      borderWidth: 2,
      data: [65, 59, 80],
    },
  ]);

  useEffect(() => {
    fetch('http://localhost:5000/allpost', {
      headers: {
        Authorization: 'Bearer ' + localStorage.getItem('jwt'),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setLabels(result.posts.map((item) => item.title));
        setElement(result.posts.map((item) => item.votes.length));
      });
  }, []);

  useEffect(() => {
    const newDataset = {
      label: 'Covid-19',
      backgroundColor: ['#B21F00', '#161750', '#12BD93', '#57BD12'],
      borderColor: 'rgba(0,0,0,1)',
      borderWidth: 2,
      data: element,
    };
    setDatasets([newDataset]);
  }, [element]);

  const stateData = {
    labels,
    datasets,
  };

  return (
    <div>
      <div style={{ height: '400px', width: '60%', marginBottom: '50px', marginTop: '50px', marginLeft: '20%' }}>
        <Pie
          data={stateData}
          options={{
            title: {
              display: true,
              text: 'Covid-19 active case',
              fontSize: 20,
            },
            legend: {
              display: true,
              position: 'right',
            },
          }}
        />
      </div>
    </div>
  );
};

export default Chart;
